import React, { Component } from "react";
import {useState} from "react";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import exampleschool from "../../assets/example-school.jpg";

/* import new plan and install SVG Management Tools for React*/
import { SvgLoader, SvgProxy } from "react-svgmt";
import groundFloorPlan from "../../assets/groundFloorPlan.svg";
import test from "../../assets/test.svg";

import "./School.css";
import "./schoolMap.css";
import "./zone.css";



class SchoolMap extends Component {
  constructor(props){
    super(props)
    this.state={
      class1Color:"white",
      class2Color:"white",
      class3Color:"white"
    }
  }
colorClick=(e)=>{
  this.setState({
    class1Color:this.props.selectedOption
  })
}
color2Click=(e)=>{
  this.setState({
    class2Color:this.props.selectedOption
  })
}
color3Click=(e)=>{
  this.setState({
    class3Color:this.props.selectedOption
  })
}

  render() {
    console.log("Color:"+this.props.selectedOption);
    return (
      <TransformWrapper>
        <TransformComponent>
          <div className="area">
          {/* <SvgLoader path={test}></SvgLoader> */}

          <svg width="320" height="321" viewBox="0 0 320 321" xmlns="http://www.w3.org/2000/svg">
          <g>
          <rect x="0.5" y="0.5" width="113" height="98" fill={this.state.class1Color} stroke="black" onClick={this.colorClick}/>
          </g>
          <g>
          <circle id="class2" cx="251" cy="138" r="68.5" stroke="black" fill={this.state.class2Color} onClick={this.color2Click}/>
          <path id="class3" d="M11.383 281.25L78.5 165L145.617 281.25H11.383Z" fill={this.state.class3Color} stroke="black" onClick={this.color3Click}/>
          </g>
          </svg>
          </div>
        </TransformComponent>
      </TransformWrapper>
    );
  }
}


export default SchoolMap;
