import React from "react";
import "./FunctionBarwithMap.css";



class FunctionBarwithMap extends React.Component{
    constructor(props){
        super(props);
        this.state = {isToggleOn: true};
        this.handleClick = this.handleClick.bind(this);
        this.modeChange = this.modeChange.bind(this);
    }

    handleClick() {
        this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn
        }));
      }

    modeChange(event){
    console.log(event.target.value);
    this.setState({
        selectionMode:event.target.value
    })
}

    render(){
        return(
            <div className="tools">

                <div className="ics"> <strong>ICS lead: </strong> {this.state.isToggleOn ? 'N/A ' : 'John Doe '}<button type="button" onClick={this.handleClick}>Assign </button></div>
            </div>
        )
    }
}

export default FunctionBarwithMap;
