import React from "react";
import "./FunctionBarwithMap.css";



class FunctionBarwithMap extends React.Component{
    constructor(props){
        super(props);
        this.state = {isToggleOn: true};
        this.handleClick = this.handleClick.bind(this);
        this.modeChange = this.modeChange.bind(this);
    }

    handleClick() {
        this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn
        }));
      }

    modeChange(event){
    console.log(event.target.value);
    this.setState({
        selectionMode:event.target.value
    })
}

    render(){
        return(
            <div className="functionMenu" onChange={this.modeChange}>
                <label>  <input className="mode" type="radio" name="mode" value = "ICSleadpoint" checked={this.selectionMode==="ICSleadpoint"}/>ICS lead point</label>

                <label>  <input type="radio" name="mode" value = "Casualtycollectionpoint" checked={this.selectionMode==="Casualtycollectionpoint"}/>Casualty collection point</label>

                <label>  <input type="radio" name="mode" value = "ZoneColor" checked={this.selectionMode==="ZoneColor"}/>Zone color</label>

                <label> <strong>ICS lead: </strong> {this.state.isToggleOn ? 'N/A ' : 'John Doe '}<button type="button" onClick={this.handleClick}>Assign </button></label>
            </div>
        )
    }
}

export default FunctionBarwithMap;
