import React from "react";
import "./FunctionBarwithMap.css";



class FunctionBarwithMap extends React.Component{
    constructor(props){
        super(props);
        this.state = {isToggleOn: true};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn
        }));
      }

    render(){
        return(
            <div className="functionMenu">
                <label>  <input type="checkbox" />ICS lead point</label>

                <label>  <input type="checkbox" />Casualty collection point</label>

                <label>  <input type="checkbox" />Zone color</label>

                <label> <strong>ICS lead: </strong> {this.state.isToggleOn ? 'N/A' : 'John Doe '}<button type="button" onClick={this.handleClick}>Assign </button></label>
            </div>
        )
    }
}

export default FunctionBarwithMap;
