import React, { Component } from "react";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import exampleschool from "../../assets/example-school.jpg";

/* import new plan and install SVG Management Tools for React*/
import { SvgLoader, SvgProxy } from "react-svgmt";
import groundFloorPlan from "../../assets/groundFloorPlan.svg";
import test from "../../assets/test.svg";

import "./School.css";
import "./schoolMap.css";
import "./zone.css";
// import Zone from "./zone";
import {useState} from "react";

class SchoolMap extends Component {
  render() {
    return (
      <div>
      <div className = "zone">
      <Zone />
      </div>
      <TransformWrapper>
        <TransformComponent>
          <div className="area">
          {/* <SvgLoader path={test}></SvgLoader> */}
          <svg width="320" height="321" viewBox="0 0 320 321" fill="none" xmlns="http://www.w3.org/2000/svg">
          <g>
          <rect id="class1" x="0.5" y="0.5" width="113" height="98" fill="white" stroke="black"/>

          <circle id="class2" cx="251" cy="138" r="68.5" stroke="black"/>
          <path id="class3" d="M11.383 281.25L78.5 165L145.617 281.25H11.383Z" fill="#D9D9D9" stroke="black"/>
          </g>
          </svg>
          </div>
        </TransformComponent>
      </TransformWrapper>
      </div>
    );
  }
}

class Zone extends React.Component{

  constructor(props){
      super(props);
      this.state = {
          visibility:true
      }

      this.toggleVisibility = this.toggleVisibility.bind(this);
      this.onChangeValue = this.onChangeValue.bind(this);

      const [style, setStyle] = useState("cont");
  }

  toggleVisibility(){
      this.setState(state=>{
          if(state.visibility == false){
              return{visibility:true}
          }
          else if(state.visibility == true){
              return{visibility:false}
          }
      })
  }

  onChangeValue(event){
      console.log(event.target.value);
      this.setState({
          selectedOption:event.target.value
      })
  }


  render(){


      if(this.state.visibility){
          return(
              <div className="zonePanel">
                  <div className="zoneTitle">Zone</div>
                  <label class="switch">
                  <input type="checkbox" id ="zonecheck" onClick={this.toggleVisibility}/>
                  <span class="slider round"></span>
                  </label>
              </div>
          )
      }
      if(this.state.visibility==false){
          return(
          <div className="zonePanel">
              <div className="zoneTitle">Zone</div>
              <label class="switch">
              <input type="checkbox" id ="zonecheck" onClick={this.toggleVisibility}/>
              <span class="slider round"></span>
              </label>
              <div className="colorPanel" onChange={this.onChangeValue}>
                  <label className="red">
                      <input className="color" type = "radio" name="color" value="red" checked={this.state.selectedOption === "red"}></input>
                  </label>
                  <label className="orange">
                      <input className="color orange" type = "radio" name="color" value="orange" checked={this.state.selectedOption === "orange"}></input>

                  </label>
                  <label className="blue"> 
                      <input className="color blue" type = "radio" name="color" value="blue" checked={this.state.selectedOption === "blue"}></input>

                  </label>
              </div>
          </div>
          )
      }



  }
}

export default SchoolMap;
